CREATE TABLE t_keragaan_pabrik  (
  'id' int(11) NOT NULL AUTO_INCREMENT,
  'hari_giling' int(255) NOT NULL,
  'tgl_giling' date NOT NULL,
  'jam' varchar(255) CHARACTER SET latin1 COLLATE latin1_swedish_ci NOT NULL,
  'digiling' double NOT NULL,
  'brix_npp' double NULL DEFAULT 0,
  'nm_persen_tebu' double NULL DEFAULT 0,
  'uap_baru' double NULL DEFAULT 0,
  'uap_bekas' double NULL DEFAULT 0,
  'suhu_pp_i' double NULL DEFAULT 0,
  'suhu_pp_ii' double NULL DEFAULT 0,
  'suhu_pp_iii' double NULL DEFAULT 0,
  'turbidity' double NULL DEFAULT 0,
  'v_eva' double NULL DEFAULT 0,
  'v_masakan' double NULL DEFAULT 0,
  'be_nk' double NULL DEFAULT 0,
  'kp' varchar(4) CHARACTER SET latin1 COLLATE latin1_swedish_ci NULL DEFAULT NULL,
  PRIMARY KEY ('id') USING BTREE
) ENGINE = InnoDB;

CREATE VIEW 'vw_keragaan_pabrik' AS SELECT count(id) id, 
hari_giling, tgl_giling, "-" as jam, avg(digiling) as digiling, avg(brix_npp) as brix_npp,
avg(nm_persen_tebu) as nm_persen_tebu, avg(uap_baru) as uap_baru, avg(uap_bekas) as uap_bekas, 
avg(suhu_pp_i) as suhu_pp_i, avg(suhu_pp_ii) as suhu_pp_ii, avg(suhu_pp_iii) as suhu_pp_iii,
avg(turbidity) as turbidity, avg(v_eva) as v_eva, avg(v_masakan) as v_masakan, avg(be_nk) as be_nk, kp
FROM t_keragaan_pabrik
GROUP BY hari_giling, tgl_giling ;
