DELIMITER $$
DROP FUNCTION IF EXISTS `get_hablur_ari`$$
CREATE FUNCTION `get_hablur_ari`(netto double,rendemen double(10,2)) RETURNS double
BEGIN

    declare hasil double;

    set hasil = netto*rendemen/100;

	return ROUND_UP(hasil,2);

    END$$
