CREATE FUNCTION `ROUND_UP`(num DECIMAL(32,16), places INT) RETURNS decimal(32,2)
    DETERMINISTIC
RETURN CASE WHEN num < 0

THEN - ceil(abs(num) * power(10, places)) / power(10, places)

ELSE ceil(abs(num) * power(10, places)) / power(10, places)

END$$
